import { useState } from 'react';
import API from '../services/api';
import { usePedidosContext } from '../context/PedidosContext';

export default function Rutas() {
  const { pedidos, rutas, conductores, cargarTodo } = usePedidosContext();
  const [nueva, setNueva] = useState({ conductor_id: '', notas: '' });

  const crearRuta = async () => {
    if (!nueva.conductor_id) return alert('Seleccione conductor');
    await API.post('/rutas', nueva);
    setNueva({ conductor_id: '', notas: '' });
    cargarTodo();
  };

  const asignar = async (rutaId, pedidoId) => {
    await API.post(`/rutas/${rutaId}/asignar/${pedidoId}`);
    await API.put(`/pedidos/${pedidoId}`, { estado: 'EN DESPACHO' });
    cargarTodo();
  };

  const quitar = async (rutaId, pedidoId) => {
    await API.post(`/rutas/${rutaId}/quitar/${pedidoId}`);
    await API.put(`/pedidos/${pedidoId}`, { estado: 'PENDIENTE' });
    cargarTodo();
  };

  const getColor = (estado) => {
    switch (estado) {
      case 'EN DESPACHO': return 'blue';
      case 'ENTREGADO': return 'green';
      default: return 'orange';
    }
  };

  return (
    <div>
      <h2>Rutas</h2>

      <div style={{ marginBottom: 20 }}>
        <select value={nueva.conductor_id} onChange={e => setNueva({ ...nueva, conductor_id: e.target.value })}>
          <option value=''>Seleccione Conductor</option>
          {conductores.map(c => (
            <option key={c._id} value={c._id}>{c.nombre}</option>
          ))}
        </select>
        <input placeholder='Notas' value={nueva.notas} onChange={e => setNueva({ ...nueva, notas: e.target.value })} style={{ marginLeft: 8 }} />
        <button onClick={crearRuta} style={{ marginLeft: 8}}>Crear Ruta</button>
      </div>

      <div>
        {rutas.map(r => (
          <div key={r._id} style={{ border: '1px solid #ddd', padding: 10, marginBottom: 15 }}>
            <strong>Conductor:</strong> {conductores.find(c => c._id === r.conductor_id)?.nombre || r.conductor_id}<br />
            <strong>Notas:</strong> {r.notas || "Sin notas"}<br />
            <strong>Pedidos asignados:</strong> {r.pedidos?.length || 0}

            <div style={{ marginTop: 10 }}>
              <em>Asignar pedidos pendientes:</em>
              {pedidos.filter(p => p.estado === 'PENDIENTE').map(p => (
                <div key={p._id} style={{ margin: 4, border: '1px solid #ccc', padding: 6 }}>
                  <p><strong>Cliente:</strong> {p.cliente}</p>
                  <p><strong>Dirección:</strong> {p.direccion}</p>
                  <p><strong>Zona:</strong> {p.zona}</p>
                  <p>
                    <strong>Estado:</strong>
                    <span style={{ color: getColor(p.estado), fontWeight: 'bold', marginLeft: 4 }}>
                      {p.estado}
                    </span>
                  </p>
                  <button onClick={() => asignar(r._id, p._id)}>Asignar</button>
                </div>
              ))}

              <em>Pedidos en la ruta:</em>
              <ul>
                {r.pedidos && r.pedidos.length > 0 ? r.pedidos.map(p => (
                  <li key={p._id || p} style={{ marginBottom: 6 }}>
                    <strong>{p.cliente}</strong>  {p.direccion || "Sin dirección"}<br />
                    Estado: <span style={{ color: getColor(p.estado), fontWeight: 'bold' }}>{p.estado}</span><br />
                    <button onClick={() => quitar(r._id, p._id || p)} style={{ marginTop: 4 }}>
                      Quitar
                    </button>
                  </li>
                )) : <li>No hay pedidos en esta ruta</li>}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
