import { useState } from 'react';
import API from '../services/api';
import { usePedidosContext } from '../context/PedidosContext';

export default function Pedidos() {
  const { pedidos, cargarTodo } = usePedidosContext();
  const [nuevo, setNuevo] = useState({ cliente: '', direccion: '', zona: '' });

  const crear = async () => {
    if (!nuevo.cliente) return alert('Cliente requerido');
    await API.post('/pedidos', nuevo);
    setNuevo({ cliente: '', direccion: '', zona: '' });
    cargarTodo();
  };

  const eliminar = async (id) => {
    if (!window.confirm('Eliminar pedido?')) return;
    await API.delete(`/pedidos/${id}`);
    cargarTodo();
  };

  const cambiarEstado = async (id, estado) => {
    await API.put(`/pedidos/${id}`, { estado });
    cargarTodo();
  };

  const getColor = (estado) => {
    switch (estado) {
      case 'EN DESPACHO': return 'blue';
      case 'ENTREGADO': return 'green';
      default: return 'orange';
    }
  };

  return (
    <div>
      <h2>Pedidos</h2>
      <div>
        <input placeholder='Cliente' value={nuevo.cliente} onChange={e => setNuevo({ ...nuevo, cliente: e.target.value })} />
        <input placeholder='Direcci√≥n' value={nuevo.direccion} onChange={e => setNuevo({ ...nuevo, direccion: e.target.value })} />
        <input placeholder='Zona' value={nuevo.zona} onChange={e => setNuevo({ ...nuevo, zona: e.target.value })} />
        <button onClick={crear}>Agregar</button>
      </div>
      <ul>
        {pedidos.map(p => (
          <li key={p._id}>
            {p.cliente} ‚Äî 
            <span style={{ color: getColor(p.estado), fontWeight: 'bold' }}>
              {p.estado}
            </span> ‚Äî 
            {p.zona}
            <button onClick={() => cambiarEstado(p._id, 'EN DESPACHO')}>üöö</button>
            <button onClick={() => cambiarEstado(p._id, 'ENTREGADO')}>‚úÖ</button>
            <button onClick={() => eliminar(p._id)}>‚ùå</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
