import { useState } from 'react';
import API from '../services/api';
import { usePedidosContext } from '../context/PedidosContext';

//Iconos
import { FaTruck, FaBoxOpen, FaTrash, FaPlus } from 'react-icons/fa';

export default function Rutas() {
  const { pedidos, rutas, conductores, cargarTodo } = usePedidosContext();
  const [nueva, setNueva] = useState({ conductor_id: '', notas: '' });

  const crearRuta = async () => {
    if (!nueva.conductor_id) return alert('Seleccione conductor');
    await API.post('/rutas', nueva);
    setNueva({ conductor_id: '', notas: '' });
    cargarTodo();
  };

  const asignar = async (rutaId, pedidoId) => {
    await API.post(`/rutas/${rutaId}/asignar/${pedidoId}`);
    await API.put(`/pedidos/${pedidoId}`, { estado: 'EN DESPACHO' });
    cargarTodo();
  };

  const quitar = async (rutaId, pedidoId) => {
    await API.post(`/rutas/${rutaId}/quitar/${pedidoId}`);
    await API.put(`/pedidos/${pedidoId}`, { estado: 'PENDIENTE' });
    cargarTodo();
  };

  const getColor = (estado) => {
    switch (estado) {
      case 'EN DESPACHO': return '#3b82f6';
      case 'ENTREGADO': return '#10b981';
      default: return '#f59e0b';
    }
  };

  return (
    <div>
      <h2>Rutas</h2>

      <div style={{ display: 'flex', gap: 8, marginBottom: 12 }}>
        <select value={nueva.conductor_id} onChange={e => setNueva({ ...nueva, conductor_id: e.target.value })}>
          <option value=''>Seleccione Conductor</option>
          {conductores.map(c => (
            <option key={c._id} value={c._id}>{c.nombre}</option>
          ))}
        </select>
        <input placeholder='Notas' value={nueva.notas} onChange={e => setNueva({ ...nueva, notas: e.target.value })} />
        <button className="assign" onClick={crearRuta}>
          <FaPlus style={{ marginRight: 4 }} /> Crear Ruta
        </button>
      </div>

      <div>
        {rutas.map(r => (
          <div key={r._id} style={{ border: '1px solid #ddd', borderRadius: 12, padding: 12, marginBottom: 15, background: '#fff' }}>
            <div><strong>Conductor:</strong> {conductores.find(c => c._id === r.conductor_id)?.nombre || r.conductor_id}</div>
            <div><strong>Notas:</strong> {r.notas || "Sin notas"}</div>
            <div><strong>Pedidos asignados:</strong> {r.pedidos?.length || 0}</div>

            <div style={{ marginTop: 10 }}>
              <em>Asignar pedidos pendientes:</em>
              {pedidos.filter(p => p.estado === 'PENDIENTE').map(p => (
                <div key={p._id} style={{ margin: 6, border: '1px solid #ccc', borderRadius: 8, padding: 8, background: '#f9fafb' }}>
                  <div><strong>Cliente:</strong> {p.cliente}</div>
                  <div><strong>Dirección:</strong> {p.direccion}</div>
                  <div><strong>Zona:</strong> {p.zona}</div>
                  <div>
                    <strong>Estado:</strong>
                    <span style={{ color: getColor(p.estado), fontWeight: 'bold', marginLeft: 4 }}>{p.estado}</span>
                  </div>
                  <button className="assign" onClick={() => asignar(r._id, p._id)} style={{ marginTop: 4 }}>
                    <FaTruck /> Asignar
                  </button>
                </div>
              ))}

              <em>Pedidos en la ruta:</em>
              <ul>
                {r.pedidos && r.pedidos.length > 0 ? r.pedidos.map(p => (
                  <li key={p._id || p} style={{ marginBottom: 6, padding: 8, borderRadius: 8, background: '#f3f4f6', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <div>
                      <strong>{p.cliente}</strong> — {p.direccion || "Sin dirección"}<br />
                      Estado: <span style={{ color: getColor(p.estado), fontWeight: 'bold' }}>{p.estado}</span>
                    </div>
                    <div style={{ display: 'flex', gap: 6 }}>
                      <button className="delete" onClick={() => quitar(r._id, p._id || p)}>
                        <FaTrash />
                      </button>
                    </div>
                  </li>
                )) : <li>No hay pedidos en esta ruta</li>}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
