import React, { useState } from 'react';
import API from '../services/api';
import { usePedidosContext } from '../context/PedidosContext';

export default function Conductores() {
  const { conductores, cargarTodo } = usePedidosContext();
  const [nuevo, setNuevo] = useState({ nombre: '', licencia: '', activo: true });

  const crear = async () => {
    if (!nuevo.nombre) return alert('Nombre requerido');
    await API.post('/conductores', nuevo);
    setNuevo({ nombre: '', licencia: '', activo: true });
    cargarTodo(); // recarga automáticamente
  };

  const eliminar = async (id) => {
    if (!window.confirm('Eliminar conductor?')) return;
    await API.delete(`/conductores/${id}`);
    cargarTodo(); // recarga automáticamente
  };

  return (
    <div>
      <h2>Conductores</h2>
      <div>
        <input 
          placeholder='Nombre' 
          value={nuevo.nombre} 
          onChange={e => setNuevo({ ...nuevo, nombre: e.target.value })} 
        />
        <input 
          placeholder='Licencia' 
          value={nuevo.licencia} 
          onChange={e => setNuevo({ ...nuevo, licencia: e.target.value })} 
        />
        <button onClick={crear}>Agregar</button>
      </div>
      <ul>
        {conductores.map(c => (
          <li key={c._id} style={{ marginTop: 8 }}>
            <strong>{c.nombre}</strong> — {c.licencia} —{' '}
            <span style={{
              color: c.activo ? 'green' : 'red',
              fontWeight: 'bold',
            }}>
              {c.activo ? 'Activo' : 'Inactivo'}
            </span>
            <button onClick={() => eliminar(c._id)} style={{ marginLeft: 10 }}>
              ❌
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
