import { useState } from 'react';
import API from '../services/api';
import { usePedidosContext } from '../context/PedidosContext';

//Iconos
import { FaTruck, FaBoxOpen, FaTrash, FaPlus } from 'react-icons/fa';

export default function Pedidos() {
  const { pedidos, cargarTodo } = usePedidosContext();
  const [nuevo, setNuevo] = useState({ cliente: '', direccion: '', zona: '' });

  const crear = async () => {
    if (!nuevo.cliente) return alert('Cliente requerido');
    await API.post('/pedidos', nuevo);
    setNuevo({ cliente: '', direccion: '', zona: '' });
    cargarTodo();
  };

  const eliminar = async (id) => {
    if (!window.confirm('Eliminar pedido?')) return;
    await API.delete(`/pedidos/${id}`);
    cargarTodo();
  };

  const cambiarEstado = async (id, estado) => {
    await API.put(`/pedidos/${id}`, { estado });
    cargarTodo();
  };

  const getColor = (estado) => {
    switch (estado) {
      case 'EN DESPACHO': return '#3b82f6'; // azul
      case 'ENTREGADO': return '#10b981';   // verde
      default: return '#f59e0b';            // naranja (pendiente)
    }
  };

  return (
    <div>
      <h2>Pedidos</h2>
      <div style={{ display: 'flex', gap: 8, marginBottom: 12 }}>
        <input placeholder='Cliente' value={nuevo.cliente} onChange={e => setNuevo({ ...nuevo, cliente: e.target.value })} />
        <input placeholder='Dirección' value={nuevo.direccion} onChange={e => setNuevo({ ...nuevo, direccion: e.target.value })} />
        <input placeholder='Zona' value={nuevo.zona} onChange={e => setNuevo({ ...nuevo, zona: e.target.value })} />
        <button className="assign" onClick={crear}>
          <FaPlus style={{ marginRight: 4 }} /> Agregar
        </button>
      </div>
      <ul>
        {pedidos.map(p => (
          <li key={p._id} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: 8, marginBottom: 6, borderRadius: 10, background: '#f9fafb' }}>
            <div>
              <div><strong>{p.cliente}</strong> — {p.zona}</div>
              <div style={{ fontSize: 13, marginTop: 2 }}>
                Estado: <span style={{ color: getColor(p.estado), fontWeight: 'bold' }}>{p.estado}</span>
              </div>
            </div>
            <div style={{ display: 'flex', gap: 6 }}>
              <button onClick={() => cambiarEstado(p._id, 'EN DESPACHO')}>
                <FaTruck color="#3b82f6" />
              </button>
              <button onClick={() => cambiarEstado(p._id, 'ENTREGADO')}>
                <FaBoxOpen color="#10b981" />
              </button>
              <button onClick={() => eliminar(p._id)}>
                <FaTrash color="#ef4444" />
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
